      "script": "# oxAuth is available under the MIT License (2008). See http://opensource.org/licenses/MIT for full text.\n# Copyright (c) 2016, Janssen\n#\n# Author: Yuriy Movchan\n#\n\nfrom io.jans.service.cdi.util import CdiUtil\nfrom io.jans.as.server.security import Identity\nfrom io.jans.model.custom.script.type.auth import PersonAuthenticationType\nfrom io.jans.as.server.service import AuthenticationService\nfrom io.jans.util import StringHelper\n\nimport java\n\nclass PersonAuthentication(PersonAuthenticationType):\n    def __init__(self, currentTimeMillis):\n        self.currentTimeMillis = currentTimeMillis\n\n    def init(self, customScript,  configurationAttributes):\n        print \"Basic. Initialization\"\n        print \"Basic. Initialized successfully\"\n        return True   \n\n    def destroy(self, configurationAttributes):\n        print \"Basic. Destroy\"\n        print \"Basic. Destroyed successfully\"\n        return True\n        \n    def getAuthenticationMethodClaims(self, requestParameters):\n        return None\n        \n    def getApiVersion(self):\n        return 11\n\n    def isValidAuthenticationMethod(self, usageType, configurationAttributes):\n        return True\n\n    def getAlternativeAuthenticationMethod(self, usageType, configurationAttributes):\n        return None\n\n    def authenticate(self, configurationAttributes, requestParameters, step):\n        authenticationService = CdiUtil.bean(AuthenticationService)\n\n        if (step == 1):\n            print \"Basic. Authenticate for step 1\"\n\n            identity = CdiUtil.bean(Identity)\n            credentials = identity.getCredentials()\n\n            user_name = credentials.getUsername()\n            user_password = credentials.getPassword()\n\n            logged_in = False\n            if (StringHelper.isNotEmptyString(user_name) and StringHelper.isNotEmptyString(user_password)):\n                logged_in = authenticationService.authenticate(user_name, user_password)\n\n            if (not logged_in):\n                return False\n\n            return True\n        else:\n            return False\n\n    def prepareForStep(self, configurationAttributes, requestParameters, step):\n        if (step == 1):\n            print \"Basic. Prepare for Step 1\"\n            return True\n        else:\n            return False\n\n    def getExtraParametersForStep(self, configurationAttributes, step):\n        return None\n\n    def getCountAuthenticationSteps(self, configurationAttributes):\n        return 1\n\n    def getPageForStep(self, configurationAttributes, step):\n        return \"\"\n\n    def getNextStep(self, configurationAttributes, requestParameters, step):\n        return -1\n\n    def getLogoutExternalUrl(self, configurationAttributes, requestParameters):\n        print \"Get external logout URL call\"\n        return None\n\n    def logout(self, configurationAttributes, requestParameters):\n        return True\n",
